import React, { useEffect, useState } from 'react'
import './Signin.css'
import axios from '../../helpers/axios'
import { Navigate } from 'react-router-dom'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { faceBookLogging, login } from '../../actions'
import FacebookLogin from 'react-facebook-login';



const Signin = (props) => {
  const [username, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const auth = useSelector(state => state.auth);
  const user = useSelector(state => state.user);
  const dispatch = useDispatch();


  const userLogin = (e) => {
    e.preventDefault();

    const user = {
      username,
      password
    }
    dispatch(login(user));

  }

  const facebookSignin = (e) => {
    e.preventDefault();

    // let name = ''
    // let email = ''

    // const user = {
    //   name,
    //   email
    // }
    dispatch(faceBookLogging());

  }


  if (auth.authenticate) {
    return <Navigate to='/' />
  }
  if (user.loading) {
    return (<p>Loading....</p>)
  }

  const responseFacebook = (response) => {
    console.log({response});

    // const data = 

    // axios({
    //   method: "POST",
    //   url: 'http://localhost:8080/api/facebooklogin',
    //   data: { accessToken: response.accessToken, userID: response.userID }
    // }).then(response => {
    //   console.log({response})
    // })

  }
  // const componentClicked = (data) => {
  //   console.warn({data})
  // }


  return (
    <>
      {/* {console.log({auth})} */}
      {/* <div className="message" style={{ marginLeft: '900px', color: 'red', fontSize: '20px' }}>{auth.error}</div> */}
      <div className="login-form">
        <h1>Login Form</h1>
        <form onSubmit={userLogin}>
          <input type="text" name="username" placeholder="Username"
            value={username}
            onChange={(e) => setUserName(e.target.value)} />
          <input type="password" name="password" placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} />
          <input type="submit" value="Log In" />
        </form>
        <br />
        <div className="facebooklogin">

          <FacebookLogin
          buttonStyle={{height: '40px', width: '300px', textAlign: 'center', paddingTop: '6px', borderRadius: '5px'}}
            appId="1217984252387809"
            autoLoad={false}
            // fields="name,email,picture"
            // onClick={facebookSignin}
            callback={responseFacebook} />,
        </div>
        <div>
          <a href="/signup">Sign Up</a>
          <br />
          <br />
          <a href="http://localhost:3000/resetpassword">Forget Password</a>
        </div>



      </div>
    </>

  )
}

export default Signin
