import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { addDepartment } from '../../actions';
import Layout from '../../Components/Layout/Layout'
import SideBar from '../../Components/SideBar/SideBar'

const AddDepartment = () => {


    const [name, setName] = useState('');
    const [description, setDescription] = useState('');


    const dispatch = useDispatch();




    const renderAddDepartmentModal = () => {

        const saveDepartment = (e) => {
            e.preventDefault();

            const form = new FormData();

            form.append('name', name);
            form.append('description', description);

            dispatch(addDepartment(form));
        }


        return (
            <>
                {<SideBar />}
                <div className="page-wrapper">
                    <div className="content">
                        <div className="row">
                            <div className="col-lg-8 offset-lg-2">
                                <h4 className="page-title">Add Department</h4>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-lg-8 offset-lg-2">
                                <form onSubmit={saveDepartment}>
                                    <div className="form-group">
                                        <label>Department Name</label>
                                        <input className="form-control" type="text" name="name"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)} />
                                    </div>
                                    <div className="form-group">
                                        <label>Description</label>
                                        <textarea cols="30" rows="4" className="form-control" name="description"
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}></textarea>
                                    </div>

                                    <div className="m-t-20 text-center">
                                        <button  className="btn btn-primary submit-btn">Add Department</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }


    return (
        <Layout>
            {renderAddDepartmentModal()}
        </Layout>
    )
}

export default AddDepartment
