import { authConstants } from "./constants"
import axios from '../helpers/axios'




//  User SignIn Action
export const login = (user) => {

    // console.log(user);

    return async (dispatch) => {


        dispatch({ type: authConstants.LOGIN_REQUEST });
        const res = await axios.post(`/signin`, {
            ...user
        });


        if (res.status === 200) {
            const { token, user } = res.data;
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));
            dispatch({
                type: authConstants.LOGIN_SUCCESS,
                payload: {
                    token,
                    user
                }
            });
        } else {
            if (res.status === 400) {
                console.log('400',res.data.error)
                dispatch({
                    type: authConstants.LOGIN_INVAILD_PASSWORD,
                    payload: {
                        error: res.data.error 
                    }
                });
            }
            if (res.status === 401) {
                console.log('401',res.data.error)
                dispatch({
                    type: authConstants.LOGIN_NOT_VARIFIED,
                    payload: {
                        error: res.data.error 
                    }
                });
            }
            if (res.status === 402) {
                console.log('402',res.data.error)
                dispatch({
                    type: authConstants.LOGIN_NOT_EXIT,
                    payload: {
                        error: res.data.error 
                    }
                });
            }
        }

    }
}


