import React, { useEffect, useState } from 'react'
import './Signin.css'
import { Navigate } from 'react-router-dom'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { faceBookLogging, googleLogin, login } from '../../actions'
import FacebookLogin from 'react-facebook-login';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import jwt_decode from "jwt-decode";
import InstagramLogin from 'react-instagram-login';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const Signin = (props) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const auth = useSelector(state => state.auth);
  const user = useSelector(state => state.user);
  const dispatch = useDispatch();


  const userLogin = (e) => {
    e.preventDefault();

    const user = {
      email,
      password
    }

    if (email === '' || password === '') {
      toast.warning('Please Fill All Form..!!', {
        position: toast.POSITION.TOP_CENTER
      });
    }
    else {
      dispatch(login(user));
      setEmail('')
      setPassword('')
    }

  }



  if (auth.authenticate) {
    return <Navigate to='/' />
  }


  if (user.loading) {
    return (<p>Loading....</p>)
  }

  const responseSuccessGoogle = (response) => {

    const user_Obj = jwt_decode(response.credential)
    console.log({ user_Obj })

    const data = {
      name: user_Obj.name,
      email: user_Obj.email

    }
    dispatch(googleLogin(data));
    // console.log(response.tokenId)
  }

  const responseErrorGoogle = (response) => {
    console.log({ response })
  }

  const responseFacebook = (response) => {
    console.log({ response });

    const data = {
      accessToken: response.accessToken,
      userID: response.userID,

    }

    dispatch(faceBookLogging(data));

    // axios({
    //   method: "POST",
    //   url: 'http://localhost:8080/api/facebooklogin',
    //   data: { accessToken: response.accessToken, userID: response.userID }
    // }).then(response => {
    //   console.log({response})
    // })

  }

  const responseInstagram = (response) => {
    console.log(response)

  }

  const googleClientId = '293387942097-e9rkoc4bv2k45l813e7b92c5tpmnpls4.apps.googleusercontent.com'








  const OtpLoginModal = () => {

    return (
      <>
        <div class="modal" tabindex="-1">
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title">Modal title</h5>
                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
              </div>
              <div class="modal-body">
                <p>Modal body text goes here.</p>
              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                <button type="button" class="btn btn-primary">Save changes</button>
              </div>
            </div>
          </div>
        </div>
      </>
    )
  }

  return (
    <>
      {/* {console.log({auth})} */}
      {/* <div className="message" style={{ marginLeft: '900px', color: 'red', fontSize: '20px' }}>{auth.error}</div> */}
      <ToastContainer />
      <div className="login-form">
        <h1>Login Form</h1>
        <form onSubmit={userLogin}>
          <input type="email" name="email" placeholder="Enter Your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} />
          <input type="password" name="password" placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} />
          <input type="submit" value="Log In" />
        </form>
        <br />

        <div class="d-grid gap-2">
          {/* <button onClick={OtpLoginModal()} >Otp Login</button> */}
          <button type="button" onClick={OtpLoginModal()} className="btn btn-primary btn-lg">Otp Login</button>

          {/* <div className="InstagramLogin">
          <InstagramLogin
            // clientId="9569b21ff9f8d5e799172f3e1aa75ea9"
            clientSecret='9569b21ff9f8d5e799172f3e1aa75ea9'
            clientId='422530439973064'
            scope='user_profile'
            redirect_uri='http://localhost:3000/signin'
            buttonText="Login With Instagram"
            onSuccess={responseInstagram}
            onFailure={responseInstagram}
          />,
        </div> */}
          {/* <br /> */}
          <div className="btn googleLogin" style={{ width: '300px', textAlign: 'center',paddingLeft: '50px' }}>
            <GoogleOAuthProvider clientId={googleClientId}>
              <GoogleLogin
                // type="standard"
                // text="Log in with Google"
                // shape='square'
                onSuccess={responseSuccessGoogle}
                onFailure={responseErrorGoogle}
                cookiePolicy={'single_host_origin'}
              />
            </GoogleOAuthProvider>
          </div>
        </div>
        <br />
        <div className="facebooklogin">

          <FacebookLogin
            buttonStyle={{ height: '40px', width: '300px', textAlign: 'center', paddingTop: '6px', borderRadius: '5px' }}
            appId="1217984252387809"
            autoLoad={false}
            // fields="name,email,picture"
            // onClick={facebookSignin}
            callback={responseFacebook} />,
        </div>
        <div>
          <a href="/signup">Sign Up</a>
          <br />
          <br />
          <a href="http://localhost:3000/resetpassword">Forget Password</a>
        </div>



      </div>
    </>

  )
}

export default Signin
