import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { addDepartment } from '../../actions';
import Layout from '../../Components/Layout/Layout'
import SideBar from '../../Components/SideBar/SideBar'

const AddDepartment = () => {


    const [deptName, setDeptName] = useState('');
    const [desc, setDesc] = useState('');


    const dispatch = useDispatch();


    const renderAddDepartmentModal = () => {

        const saveDepartment = () => {

            const form = new FormData();

            form.append('deptName', deptName);
            form.append('desc', desc);

            dispatch(addDepartment(form));
        }


        return (
            <>
                {<SideBar />}
                <div class="page-wrapper">
                    <div class="content">
                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <h4 class="page-title">Add Department</h4>
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <form onSubmit={saveDepartment}>
                                    <div class="form-group">
                                        <label>Department Name</label>
                                        <input class="form-control" type="text" name="deptName"
                                        value={deptName}
                                        onChange={(e) => setDeptName(e.target.value)} />
                                    </div>
                                    <div class="form-group">
                                        <label>Description</label>
                                        <textarea cols="30" rows="4" class="form-control" name="desc"
                                        value={desc}
                                        onChange={(e) => setDesc(e.target.value)}></textarea>
                                    </div>

                                    <div class="m-t-20 text-center">
                                        <button class="btn btn-primary submit-btn">Add Department</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }


    return (
        <Layout>
            {renderAddDepartmentModal()}
        </Layout>
    )
}

export default AddDepartment
