import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { addEmployee } from '../../actions';
import Layout from '../../Components/Layout/Layout'
import SideBar from '../../Components/SideBar/SideBar'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const AddEmplyoee = () => {


    // const [name, setName] = useState('');
    // const [email, setEmail] = useState('');
    // const [contact, setContact] = useState('');
    // const [salary, setSalary] = useState('');
    // const [joindate, setJoinDate] = useState('');
    // const [role, setRole] = useState('');

    const dispatch = useDispatch();


    const [employees , setEmployees ] = useState({
        firstName: '', lastName: '',  email: '', dob: '', gender: '', address: '',
        image: '', department: '', role: '', joinDate: '', resignDate: '', contact: '', salary: ''
    })

    let name, value;

    const handleSave = (e) => {
        e.preventDefault();

        name = e.target.name;
        value = e.target.value;

        setEmployees({ ...employees, [name]: value });

    }

    const insertEmployee = (e) => {
        e.preventDefault();

        if (employees.firstName === '' || employees.lastName === '' || employees.email === '' || employees.dob === '' || employees.gender === '' || 
            employees.address === '' || employees.department === '' || employees.resignDate === '' || employees.image === '' ||
            employees.joindate === '' || employees.role === '' || employees.contact === '' || employees.salary === '' 

         ) {
            toast.warning('Please Fill All Form..!!', {
                position: toast.POSITION.TOP_CENTER
            });
        } else {
            console.log({employees})
            dispatch(addEmployee(employees));
            toast.success('Employee Added Successfully...', {
                position: toast.POSITION.TOP_RIGHT
            });
            setEmployees({
                firstName: '', lastName: '',  email: '', dob: '', gender: '', address: '',
                department: '', role: '', joinDate: '', resignDate: '', contact: '', salary: '', image: ''
            })
        }



    }


    const renderAddEmployeeModal = () => {


        // const saveMedicine = () => {

        //     const form = new FormData();

        //     form.append('name', name);
        //     form.append('email', email);
        //     form.append('contact', contact);
        //     form.append('salary', salary);
        //     form.append('joinDate', joindate);
        //     form.append('role', role);

        //     console.log({form});


        //     dispatch(addEmployee(form));
        // }



        return (
            <>
                {<SideBar />}
                <div class="page-wrapper">
                <ToastContainer />
                    <div class="content">
                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <h4 class="page-title">Add Employee</h4>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-lg-8 offset-lg-2">
                                {/* method="POST" encType="multipart/form-data" */}
                                <form onSubmit={handleSave}>
                                    <div className="row">
                                        <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>First Name <span className="text-danger">*</span></label>
                                                <input className="form-control" type="text" name="firstname"
                                                    value={firstname} onChange={(e) => setFirstName(e.target.value)} />
                                            </div>
                                        </div>
                                        <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>Last Name</label>
                                                <input className="form-control" type="text" name="lastname"
                                                    value={lastname} onChange={(e) => setLastName(e.target.value)} />
                                            </div>
                                        </div>
                                        {/* <div className="col-sm-6">

                                        </div> */}
                                        <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>Email <span className="text-danger">*</span></label>
                                                <input className="form-control" type="email" name="email"
                                                    value={email} onChange={(e) => setEmail(e.target.value)} />
                                            </div>
                                        </div>

                                        <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>Number of Patients <span className="text-danger">*</span></label>
                                                <input className="form-control" type="text" name="patient"
                                                    value={patient} onChange={(e) => setPatient(e.target.value)} />
                                            </div>
                                        </div>


                                        <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>Date of Birth</label>
                                                <div className="cal-icon">
                                                    <input type="text" className="form-control datetimepicker" name="dob"
                                                        value={dob} onChange={(e) => setDob(e.target.value)} />
                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-sm-6">
                                            <div className="form-group gender-select">
                                                <label className="gen-label">Gender:</label>
                                                <div className="form-check-inline">
                                                    <label className="form-check-label">
                                                        <input type="radio" name="gender" className="form-check-input"
                                                            value='Male' onChange={(e) => setGender(e.target.value)}
                                                        />Male
                                                    </label>
                                                </div>
                                                <div className="form-check-inline">
                                                    <label className="form-check-label">
                                                        <input type="radio" name="gender" className="form-check-input"
                                                            value='Female' onChange={(e) => setGender(e.target.value)}
                                                        />Female
                                                    </label>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-sm-12">
                                            <div className="row">
                                                <div className="col-sm-12">
                                                    <div className="form-group">
                                                        <label>Address</label>
                                                        <input type="text" className="form-control " name="address" value={address} onChange={(e) => setAddress(e.target.value)} />
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>Phone </label>
                                                <input className="form-control" type="text" name="phone"
                                                    value={phone} onChange={(e) => setPhone(e.target.value)} />
                                            </div>
                                        </div>

                                        <div className="col-sm-6 col-md-6 col-lg-3">
                                            <div className="form-group">
                                                <label>Department</label>

                                                <select className="" name="department"
                                                    value={department} onChange={(e) => setDepartment(e.target.value)}>

                                                    <option selected>Select</option>

                                                    {departmentName.departments.length > 0 ? departmentName.departments.map(department =>

                                                        <option value={department.name}>{department.name}</option>

                                                    ) : null}
                                                    {/* <option value="Dentists">Dentists</option>
                                                    <option value="Neurology">Neurology</option>
                                                    <option value="Cardiology">Cardiology</option>
                                                    <option value="Opthalmology">Opthalmology</option>
                                                    <option value="Orthopedics">Orthopedics</option>
                                                    <option value="Cancer Department">Cancer Department</option>
                                                    <option value="ENT Department">ENT Department</option> */}
                                                </select>

                                            </div>
                                        </div>

                                        <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>Avatar</label>
                                                <div className="profile-upload">
                                                    <div className="upload-img">
                                                        <img alt="" src="/assets/img/user.jpg" />
                                                    </div>
                                                    <div className="upload-input">
                                                        {/* <img src="" alt="" srcset="" /> */}
                                                        <input type="file" className="form-control"
                                                            name="image" onChange={handleDoctorImage} />
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="m-t-20 text-center">
                                        <button className="btn btn-primary submit-btn">Add Doctor</button>
                                    </div>
                                </form>
                            </div>
                        </div>



















                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <form onSubmit={insertEmployee}>
                                    <div class="row">
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label> Name <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control"
                                                    type="text"
                                                    name="name"
                                                    value={employees.name}
                                                    onChange={handleSave}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Email</label>
                                                <input 
                                                    class="form-control" 
                                                    type="email" 
                                                    name="email"
                                                    value={employees.email} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label> Contact <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="contact"
                                                    value={employees.contact} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>

                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label> Salary <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="salary" 
                                                    value={employees.salary} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Join Date <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="joindate" 
                                                    value={employees.joindate} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>

                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Role</label>
                                                <select class="" name="role" value={employees.role} 
                                                 onChange={handleSave}>

                                                    <option value="Nurse">Nurse</option>
                                                    <option value="Laboratorist">Laboratorist</option>
                                                    <option value="Pharmacist">Pharmacist</option>
                                                    <option value="Accountant">Accountant</option>
                                                    <option value="Receptionist">Receptionist</option>
                                                </select>
                                            </div>
                                        </div>
                                    </div>

                                    <div class="m-t-20 text-center">
                                        <button class="btn btn-primary submit-btn">Create Employee</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }


    return (
        <Layout>
            {renderAddEmployeeModal()}
        </Layout>
    )
}

export default AddEmplyoee
