import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import SideBar from '../../Components/SideBar/SideBar'
import Layout from '../Layout/Layout'



const Profile = () => {

    const auth = useSelector(state => state.auth);

    const [username, setUserName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');


    useEffect(() => {
        if (auth.authenticate) {
            setUserName(auth.user.username)
            setEmail(auth.user.email)
        }


    }, [auth.authenticate])



    const rederProfileModal = () => {



        const handleUpdateData = () => {

            const form = new FormData();

            form.append('firstname', firstname);
            form.append('lastname', lastname);
            form.append('username', username);
            form.append('email', email);
            form.append('dob', dob);
            form.append('gender', gender);
            form.append('address', address);
            form.append('password', password);
            form.append('newpassword', newPassword);

            // console.log({form});

            // dispatch(addEmployee(form));
        }


        return (
            <>
                {<SideBar />}
                <div class="page-wrapper">
                    <div class="content">
                        <div class="row">
                            <div class="col-sm-7 col-6">
                                <h4 class="page-title">My Profile</h4>
                            </div>

                            <div class="col-sm-5 col-6 text-right m-b-30">

                            </div>
                        </div>
                        <div class="card-box profile-header">
                            <div class="row">
                                <form onSubmit={handleUpdateData}>
                                    <div className="col-sm-6">
                                        <div className="form-group">
                                            <label>First Name <span className="text-danger">*</span></label>
                                            <input className="form-control" type="text" name="firstname"
                                                value={firstname} onChange={(e) => setFirstName(e.target.value)} />
                                        </div>
                                    </div>
                                    <div className="col-sm-6">
                                        <div className="form-group">
                                            <label>Last Name</label>
                                            <input className="form-control" type="text" name="lastname"
                                                value={lastname} onChange={(e) => setLastName(e.target.value)} />
                                        </div>
                                    </div>
                                    <div class="form-group">
                                        <label for="">Username</label>
                                        <input class="form-control" type="text" name="username" value={username}
                                            onChange={(e) => setUserName(e.target.value)}
                                        />
                                    </div>
                                    <div class="form-group">
                                        <label for="">Email</label>
                                        <input class="form-control" type="email" name="email" value={email}
                                            onChange={(e) => setEmail(e.target.value)} />
                                    </div>
                                    <div className="col-sm-6">
                                            <div className="form-group">
                                                <label>Date of Birth</label>
                                                <div className="cal-icon">
                                                    <input type="text" className="form-control datetimepicker" name="dob"
                                                        value={dob} onChange={(e) => setDob(e.target.value)} />
                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-sm-6">
                                            <div className="form-group gender-select">
                                                <label className="gen-label">Gender:</label>
                                                <div className="form-check-inline">
                                                    <label className="form-check-label">
                                                        <input type="radio" name="gender" className="form-check-input"
                                                            value='Male' onChange={(e) => setGender(e.target.value)}
                                                        />Male
                                                    </label>
                                                </div>
                                                <div className="form-check-inline">
                                                    <label className="form-check-label">
                                                        <input type="radio" name="gender" className="form-check-input"
                                                            value='Female' onChange={(e) => setGender(e.target.value)}
                                                        />Female
                                                    </label>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-sm-12">
                                            <div className="row">
                                                <div className="col-sm-12">
                                                    <div className="form-group">
                                                        <label>Address</label>
                                                        <input type="text" className="form-control " name="address" value={address} onChange={(e) => setAddress(e.target.value)} />
                                                    </div>
                                                </div>
                                            </div>
                                        </div>

                                    <div class="form-group">
                                        <label for="">password</label>
                                        <input class="form-control" type="text" name="password"
                                            value={password}
                                            onChange={(e) => setPassword(e.target.value)} />
                                    </div>

                                    <div class="form-group">
                                        <label for="">New Password</label>
                                        <input class="form-control" type="text" name="new_password"
                                            value={newPassword}
                                            onChange={(e) => setNewPassword(e.target.value)} />
                                    </div>

                                    <button class="btn btn-primary btn-rounded">Edit Profile</button>
                                </form>

                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }


    return (
        <Layout>
            {rederProfileModal()}
        </Layout>
    )
}

export default Profile
