import React, { useEffect, useState } from 'react'
import './Signin.css'
import axios from '../../helpers/axios'
import { Navigate } from 'react-router-dom'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { faceBookLogging, googleLogin, login } from '../../actions'
import FacebookLogin from 'react-facebook-login';
// import GoogleLogin from 'react-google-login'
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import jwt_decode from "jwt-decode";
import InstagramLogin from 'react-instagram-login';



const Signin = (props) => {
  const [username, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const auth = useSelector(state => state.auth);
  const user = useSelector(state => state.user);
  const dispatch = useDispatch();


  const userLogin = (e) => {
    e.preventDefault();

    const user = {
      username,
      password
    }
    dispatch(login(user));

  }



  if (auth.authenticate) {
    return <Navigate to='/' />
  }
  if (user.loading) {
    return (<p>Loading....</p>)
  }

  const responseSuccessGoogle = (response) => {

    const user_Obj = jwt_decode(response.credential)
    console.log({ user_Obj })

    const data = {
      name: user_Obj.name,
      email: user_Obj.email

    }
    dispatch(googleLogin(data));
    // console.log(response.tokenId)
  }

  const responseErrorGoogle = (response) => {
    console.log({ response })
  }

  const responseFacebook = (response) => {
    console.log({ response });

    const data = {
      accessToken: response.accessToken,
      userID: response.userID,

    }

    dispatch(faceBookLogging(data));

    // axios({
    //   method: "POST",
    //   url: 'http://localhost:8080/api/facebooklogin',
    //   data: { accessToken: response.accessToken, userID: response.userID }
    // }).then(response => {
    //   console.log({response})
    // })

  }

  const responseInstagram = (response) => {
    console.log(response)

  }

  const googleClientId = '293387942097-e9rkoc4bv2k45l813e7b92c5tpmnpls4.apps.googleusercontent.com'

  return (
    <>
      {/* {console.log({auth})} */}
      {/* <div className="message" style={{ marginLeft: '900px', color: 'red', fontSize: '20px' }}>{auth.error}</div> */}
      <div className="login-form">
        <h1>Login Form</h1>
        <form onSubmit={userLogin}>
          <input type="text" name="username" placeholder="Username"
            value={username}
            onChange={(e) => setUserName(e.target.value)} />
          <input type="password" name="password" placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} />
          <input type="submit" value="Log In" />
        </form>
        <br />
        <div className="InstagramLogin">
          <InstagramLogin
            // clientId="9569b21ff9f8d5e799172f3e1aa75ea9"
            clientSecret='9569b21ff9f8d5e799172f3e1aa75ea9'
            clientId='422530439973064'
            scope='user_profile'
            redirect_uri='http://localhost:3000/signin'
            buttonText="Login With Instagram"
            onSuccess={responseInstagram}
            onFailure={responseInstagram}
          />,
        </div>
        <br />
        <div className="googleLogin" style={{ width: '300px', textAlign: 'center' }}>
          <GoogleOAuthProvider clientId={googleClientId}>
            <GoogleLogin
              // type="standard"
              // text="Log in with Google"
              // shape='square'
              onSuccess={responseSuccessGoogle}
              onFailure={responseErrorGoogle}
              cookiePolicy={'single_host_origin'}
            />
          </GoogleOAuthProvider>
        </div>
        <br />
        <div className="facebooklogin">

          <FacebookLogin
            buttonStyle={{ height: '40px', width: '300px', textAlign: 'center', paddingTop: '6px', borderRadius: '5px' }}
            appId="1217984252387809"
            autoLoad={false}
            // fields="name,email,picture"
            // onClick={facebookSignin}
            callback={responseFacebook} />,
        </div>
        <div>
          <a href="/signup">Sign Up</a>
          <br />
          <br />
          <a href="http://localhost:3000/resetpassword">Forget Password</a>
        </div>



      </div>
    </>

  )
}

export default Signin
