import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import Layout from '../../Components/Layout/Layout'
import SideBar from '../../Components/SideBar/SideBar'

const AddDepartment = () => {


    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [contact, setContact] = useState('');
    const [salary, setSalary] = useState('');
    const [joindate, setJoinDate] = useState('');
    const [role, setRole] = useState('');

    const dispatch = useDispatch();


    const renderAddDepartmentModal = () => {

        const saveDepartment = () => {

            const form = new FormData();

            form.append('name', name);
            form.append('email', email);
            form.append('contact', contact);
            form.append('salary', salary);
            form.append('joindate', joindate);
            form.append('role', role);

            console.log({form});


            dispatch(addEmployee(form));
        }


        return (
            <>
                {<SideBar />}
                <div class="page-wrapper">
                    <div class="content">
                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <h4 class="page-title">Add Department</h4>
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <form action="/home/add_departments" method="POST">
                                    <div class="form-group">
                                        <label>Department Name</label>
                                        <input class="form-control" type="text" name="dpt_name" />
                                    </div>
                                    <div class="form-group">
                                        <label>Description</label>
                                        <textarea cols="30" rows="4" class="form-control" name="desc"></textarea>
                                    </div>

                                    <div class="m-t-20 text-center">
                                        <button class="btn btn-primary submit-btn">Add Department</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }


    return (
        <Layout>
            {renderAddDepartmentModal()}
        </Layout>
    )
}

export default AddDepartment
