import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { addPatient } from '../../actions';
import Layout from '../../Components/Layout/Layout'
import SideBar from '../../Components/SideBar/SideBar'



const AddPatient = () => {



    const dispatch = useDispatch();
    const patient = useSelector(state => state.patient);
    const [message, setMessage] = useState('');







    const [patients , setPatients ] = useState({
        patientName: '', age: '', admitDate: '', dischargeDate: '', address: '', contact: '', department: ''
    })

    let name, value;

    const handleSave = (e) => {
        e.preventDefault();

        name = e.target.name;
        value = e.target.value;

        setPatients({ ...patients, [name]: value });

    }

    const insertEmployee = (e) => {
        e.preventDefault();

        console.log({patients})
        dispatch(addPatient(patients));
    }

    const showMessge = () => {

        setMessage(department.message);
        // setError(department.error);

        setTimeout(() => {
            setMessage('');
            setDepartments({
                name: '', description: ''
            })
            // setError('');
        }, 3000);

    }



    const renderAddPatientModal = () => {


        // const savePatient = () => {

        //     const form = new FormData();

        //     form.append('name', name);
        //     form.append('email', email);
        //     form.append('contact', contact);
        //     form.append('salary', salary);
        //     form.append('joinDate', joindate);
        //     form.append('role', role);

        //     console.log({form});
        //     dispatch(addEmployee(form));
        // }


        return (
            <>
                {<SideBar />}
                <div class="page-wrapper">
                <div style={{ marginLeft: '700px', color: 'green' }}><span>{message}</span></div>

                    <div class="content">
                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <h4 class="page-title">Add Patient</h4>
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-lg-8 offset-lg-2">
                                <form onSubmit={insertEmployee}>
                                    <div class="row">
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label> Patient Name <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control"
                                                    type="text"
                                                    name="patientName"
                                                    value={patients.patientName}
                                                    onChange={handleSave}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Age</label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="age"
                                                    value={patients.age} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label> Admit Date <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="admitDate"
                                                    value={patients.admitDate} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>

                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label> Discharge Date <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="dischargeDate" 
                                                    value={patients.dischargeDate} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Address <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="address" 
                                                    value={patients.address} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Contact <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="contact" 
                                                    value={patients.contact} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>
                                        <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Department <span class="text-danger">*</span></label>
                                                <input 
                                                    class="form-control" 
                                                    type="text" 
                                                    name="department" 
                                                    value={patients.department} 
                                                    onChange={handleSave}
                                                    />
                                            </div>
                                        </div>

                                        {/* <div class="col-sm-6">
                                            <div class="form-group">
                                                <label>Role</label>
                                                <select class="" name="role" value={role} 
                                                 onChange={(e) => setRole(e.target.value)}>

                                                    <option value="Nurse">Nurse</option>
                                                    <option value="Laboratorist">Laboratorist</option>
                                                    <option value="Pharmacist">Pharmacist</option>
                                                    <option value="Accountant">Accountant</option>
                                                    <option value="Receptionist">Receptionist</option>
                                                </select>
                                            </div>
                                        </div> */}
                                    </div>

                                    <div class="m-t-20 text-center">
                                        <button class="btn btn-primary submit-btn">Create Employee</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }


    return (
        <Layout>
            {renderAddPatientModal()}
        </Layout>
    )
}

export default AddPatient
